import React, { useState } from "react";

const Login = () => {
  const [usuario, setUsuario] = useState("");
  const [contrasena, setContrasena] = useState("");
  const [error, setError] = useState("");
  const [olvideContrasena, setOlvideContrasena] = useState(false);

  const handleUsuarioChange = (e) => {
    setUsuario(e.target.value);
  };

  const handleContrasenaChange = (e) => {
    setContrasena(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (usuario.trim() === "" || contrasena.trim() === "") {
      setError("Por favor ingrese usuario y contraseña");
      return;
    }
    if (contrasena.length < 6) {
      setError("La contraseña debe tener al menos 6 caracteres");
      return;
    }
    // Aquí puedes realizar la lógica para iniciar sesión, como enviar los datos al servidor
    alert("Inicio de sesión exitoso");
  };

  const handleOlvideContrasena = () => {
    // Aquí puedes implementar la lógica para manejar el caso de "Olvidé mi contraseña"
    alert(
      "Se ha enviado un correo electrónico para restablecer la contraseña."
    );
  };

  return (
    <div className="container mx-auto">
      <div className="flex flex-col items-center justify-center h-screen">
        <div className="w-full max-w-md">
          <div className="bg-white shadow-md rounded-md p-6">
            <h1 className="text-center text-2xl font-bold mb-4">
              Iniciar Sesión
            </h1>
            <form onSubmit={handleSubmit}>
              <div className="mb-6">
                <label
                  htmlFor="usuario"
                  className="block text-gray-700 text-sm font-bold mb-2"
                >
                  Usuario
                </label>
                <input
                  type="text"
                  id="usuario"
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  value={usuario}
                  onChange={handleUsuarioChange}
                />
              </div>
              <div className="mb-6">
                <label
                  htmlFor="contrasena"
                  className="block text-gray-700 text-sm font-bold mb-2"
                >
                  Contraseña
                </label>
                <input
                  type="password"
                  id="contrasena"
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  value={contrasena}
                  onChange={handleContrasenaChange}
                />
              </div>
              {error && <p className="text-red-500 text-sm mb-3">{error}</p>}
              <div className="flex items-center justify-between">
                <button
                  type="submit"
                  className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  Iniciar Sesión
                </button>
                <button
                  type="button"
                  onClick={handleOlvideContrasena}
                  className="text-gray-700 text-sm focus:outline-none"
                >
                  ¿Olvidó su contraseña?
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
