import React, { useState } from "react";

const Navbar = () => {
  return (
    <nav className="flex justify-between items-center bg-gray-100 p-4">
      <div className="flex items-center">
        <div className="w-12 h-12 bg-gray-500 rounded-full mr-2 flex items-center justify-center">
          <svg
            className="w-6 h-6 text-white"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M12 14l9-5-9-5-9 5 9 5z"
            ></path>
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M12 14l9-5-9-5-9 5 9 5z"
            ></path>
          </svg>
        </div>
        <span className="text-gray-800 font-bold mr-4">Cliente</span>
      </div>
      <div className="flex items-center space-x-4">
        <a
          href="#"
          className="text-gray-700 relative hover:bg-gray-800 hover:text-gray-00 rounded-full p-2 transition duration-300 ease-in-out"
        >
          <span className="inline-block">Inicio</span>
          <span className="absolute inset-0 bg-gray-200 opacity-0  transition-opacity duration-300 ease-in-out rounded-full"></span>
        </a>
        <a
          href="#"
          className="text-gray-700 relative hover:bg-gray-300 hover:text-gray-900 rounded-full p-2 transition duration-300 ease-in-out"
        >
          <span className="inline-block">Perfil</span>
          <span className="absolute inset-0 bg-gray-200 opacity-0 hover:opacity-100 transition-opacity duration-300 ease-in-out rounded-full"></span>
        </a>
        <a
          href="#"
          className="text-gray-700 relative hover:bg-gray-300 hover:text-gray-900 rounded-full p-2 transition duration-300 ease-in-out"
        >
          <span className="inline-block">Registro de Departamentos</span>
          <span className="absolute inset-0 bg-gray-200 opacity-0 hover:opacity-100 transition-opacity duration-300 ease-in-out rounded-full"></span>
        </a>
        <a
          href="#"
          className="text-gray-700 relative hover:bg-gray-300 hover:text-gray-900 rounded-full p-2 transition duration-300 ease-in-out"
        >
          <span className="inline-block">Administración de Tickets</span>
          <span className="absolute inset-0 bg-gray-200 opacity-0 hover:opacity-100 transition-opacity duration-300 ease-in-out rounded-full"></span>
        </a>
      </div>
    </nav>
  );
};

const App = () => {
  const [nombre, setNombre] = useState("Fernando Mauricio Castillo Rosales");
  const [contrasena, setContrasena] = useState("");
  const [departamento, setDepartamento] = useState("");

  const handleNombreChange = (e) => {
    setNombre(e.target.value);
  };

  const handleContrasenaChange = (e) => {
    setContrasena(e.target.value);
  };

  const handleDepartamentoChange = (e) => {
    setDepartamento(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Formulario enviado");
  };

  return (
    <div>
      <Navbar />
      <div className="container mx-auto">
        <div className="flex flex-col items-center justify-center h-screen">
          <div className="w-full max-w-md">
            <div className="bg-white shadow-md rounded-md p-6">
              <h1 className="text-center text-2xl font-bold mb-4">
                Perfil de Usuario
              </h1>
              <form onSubmit={handleSubmit}>
                <div className="mb-6">
                  <label
                    htmlFor="nombre"
                    className="block text-gray-700 text-sm font-bold mb-2"
                  >
                    Nombre
                  </label>
                  <input
                    type="text"
                    id="nombre"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={nombre}
                    onChange={handleNombreChange}
                  />
                </div>
                <div className="mb-6">
                  <label
                    htmlFor="contrasena"
                    className="block text-gray-700 text-sm font-bold mb-2"
                  >
                    Contraseña
                  </label>
                  <input
                    type="password"
                    id="contrasena"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={contrasena}
                    onChange={handleContrasenaChange}
                  />
                </div>
                <div className="mb-6">
                  <label
                    htmlFor="departamento"
                    className="block text-gray-700 text-sm font-bold mb-2"
                  >
                    Departamento
                  </label>
                  <select
                    id="departamento"
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    value={departamento}
                    onChange={handleDepartamentoChange}
                  >
                    <option value="">Seleccione un departamento</option>
                    <option value="Ventas">Ventas</option>
                    <option value="Marketing">Marketing</option>
                    <option value="Desarrollo">Desarrollo</option>
                  </select>
                </div>
                <div className="flex items-center justify-center">
                  <button
                    type="submit"
                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  >
                    Editar
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
