import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser } from "@fortawesome/free-solid-svg-icons";

const Perfil = () => {
  const [nombre, setNombre] = useState("Fernando Mauricio Castillo Rosales");
  const [contrasena, setContrasena] = useState("");
  const [departamento, setDepartamento] = useState("");

  const handleNombreChange = (e) => {
    setNombre(e.target.value);
  };

  const handleContrasenaChange = (e) => {
    setContrasena(e.target.value);
  };

  const handleDepartamentoChange = (e) => {
    setDepartamento(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Formulario enviado");
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <div className="p-4 mt-4 text-6xl font-bold ">Perfil de Usuario</div>

      <div className="w-full max-w-3xl">
        <div className="bg-white shadow-md rounded-md p-6">
          <div className="flex items-center justify-center mb-4">
            <div className="-mt-16 w-32 h-32 bg-gray-300 rounded-full overflow-hidden flex items-center justify-center z-10">
              <FontAwesomeIcon
                icon={faUser}
                className="text-5xl text-gray-500"
              />
            </div>
          </div>

          <form onSubmit={handleSubmit}>
            <div className="mb-6">
              <label
                htmlFor="nombre"
                className="block text-gray-700 text-sm font-bold mb-2"
              >
                Nombre
              </label>
              <input
                type="text"
                id="nombre"
                placeholder="Nombre"
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                value={nombre}
                onChange={handleNombreChange}
              />
            </div>
            <div className="mb-6">
              <label
                htmlFor="contrasena"
                className="block text-gray-700 text-sm font-bold mb-2"
              >
                Contraseña
              </label>
              <input
                type="password"
                id="contrasena"
                placeholder="Contraseña"
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                value={contrasena}
                onChange={handleContrasenaChange}
              />
            </div>
            <div className="mb-6">
              <label
                htmlFor="departamento"
                className="block text-gray-700 text-sm font-bold mb-2"
              >
                Departamento
              </label>
              <select
                id="departamento"
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                value={departamento}
                onChange={handleDepartamentoChange}
              >
                <option value="">Seleccione un departamento</option>
                <option value="Ventas">Compras</option>
                <option value="Marketing">Contabilidad</option>
                <option value="Desarrollo">Logistica</option>
                <option value="Desarrollo">Produccion</option>
                <option value="Desarrollo">Ventas</option>
              </select>
            </div>
            <div className="flex items-center justify-center">
              <button
                type="submit"
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              >
                Editar
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Perfil;
