import React, { useState } from "react";
import Swal from "sweetalert2";

const ConsultarTickets = () => {
  const [tickets, setTickets] = useState([
    // Arreglo de tickets
  ]);

  const [selectedTicket, setSelectedTicket] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleConsultar = (ticket) => {
    setSelectedTicket(ticket);
    setIsModalOpen(true);
  };

  const confirmarCancelar = (id) => {
    Swal.fire({
      title: "¿Estás seguro?",
      text: "¿Quieres cancelar este ticket?",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Sí, cancelar",
      cancelButtonText: "Cancelar",
    }).then((result) => {
      if (result.isConfirmed) {
        handleCancelar(id);
        setIsModalOpen(false); // Cerrar el modal después de cancelar
      }
    });
  };

  const handleCancelar = (id) => {
    const updatedTickets = tickets.map((ticket) => {
      if (ticket.id === id) {
        if (ticket.status === "Asignado") {
          Swal.fire({
            icon: "error",
            title: "Oops...",
            text: "No puedes cancelar un ticket asignado.",
          });
          return ticket;
        } else {
          return { ...ticket, status: "Cancelado" };
        }
      }
      return ticket;
    });
    setTickets(updatedTickets);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  return (
    <table className="table-auto w-full">
      <thead>{/* Encabezados de la tabla */}</thead>
      <tbody>
        {tickets.map((ticket) => (
          <tr key={ticket.id}>{/* Contenido de cada fila */}</tr>
        ))}
      </tbody>
      {isModalOpen && selectedTicket && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
          <div className="bg-white p-6 rounded-md">
            <h2 className="text-2xl font-bold mb-4">Detalles del Ticket</h2>
            {/* Contenido del modal */}
          </div>
        </div>
      )}
    </table>
  );
};

export default ConsultarTickets;
