import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faBars,
  faCog,
  faSun,
  faMoon,
  faCircle,
} from "@fortawesome/free-solid-svg-icons";

function App() {
  return (
    <div>
      {/* Navbar */}
      <nav className="bg-gray-800 p-4 flex justify-between items-center">
        {/* Logo */}
        <div className="flex items-center space-x-4">
          <div className="bg-blue-500 text-white font-bold px-2 py-1 rounded-l">
            Cliente
          </div>
          <div className="flex items-center space-x-4">
            <div className="bg-green-500 text-white px-2 py-1 rounded-l">
              Inicio
            </div>
            <div className="bg-yellow-500 text-white px-2 py-1 rounded-l">
              Perfil
            </div>
            <div className="bg-red-500 text-white px-2 py-1 rounded-l">
              Registro
            </div>
            <div className="bg-purple-500 text-white px-2 py-1 rounded-l">
              Departamentos
            </div>
            <div className="bg-indigo-500 text-white px-2 py-1 rounded-l">
              Administración de Tickets
            </div>
          </div>
        </div>
        {/* Icons */}
        <div className="flex space-x-4">
          <FontAwesomeIcon icon={faCog} className="text-white" />
          <FontAwesomeIcon icon={faSun} className="text-white" />
          <FontAwesomeIcon icon={faMoon} className="text-white" />
          <FontAwesomeIcon icon={faCircle} className="text-white" />
        </div>
      </nav>
      {/* Main Content */}
      <div className="p-4">
        {/* Aquí va el contenido principal de tu aplicación */}
      </div>
    </div>
  );
}

export default App;
