import React, { useState } from "react";

const Navbar = () => {
  return (
    <nav className="flex justify-between items-center bg-gray-100 p-4">
      <div className="flex items-center">
        <div className="w-12 h-12 bg-gray-500 rounded-full mr-2 flex items-center justify-center">
          <svg
            className="w-6 h-6 text-white"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M12 14l9-5-9-5-9 5 9 5z"
            ></path>
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M12 14l9-5-9-5-9 5 9 5z"
            ></path>
          </svg>
        </div>
        <span className="text-gray-800 font-bold mr-4">Cliente</span>
      </div>
      <div className="flex items-center space-x-4">
        <a href="#" className="text-gray-800 hover:text-gray-600">
          Inicio
        </a>
        <a href="#" className="text-gray-800 hover:text-gray-600">
          Perfil
        </a>
        <a href="#" className="text-gray-800 hover:text-gray-600">
          Registro de Departamentos
        </a>
        <a href="#" className="text-gray-800 hover:text-gray-600">
          Administración de Tickets
        </a>
      </div>
    </nav>
  );
};

const TicketTable = () => {
  const [tickets, setTickets] = useState([
    {
      id: 1,
      fecha: "2024-03-12",
      descripcion: "Descripción del ticket 1",
      status: "Pendiente",
    },
    {
      id: 2,
      fecha: "2024-03-13",
      descripcion: "Descripción del ticket 2",
      status: "Asignado",
    },
    {
      id: 3,
      fecha: "2024-03-14",
      descripcion: "Descripción del ticket 3",
      status: "Pendiente",
    },
  ]);

  const [selectedTicket, setSelectedTicket] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleConsultar = (ticket) => {
    setSelectedTicket(ticket);
    setIsModalOpen(true);
  };

  const confirmarCancelar = (id) => {
    if (window.confirm("¿Estás seguro de que quieres cancelar este ticket?")) {
      handleCancelar(id);
      setIsModalOpen(false);
    }
  };

  const handleCancelar = (id) => {
    const updatedTickets = tickets.map((ticket) => {
      if (ticket.id === id) {
        if (ticket.status === "Asignado") {
          alert("No puedes cancelar un ticket asignado.");
          return ticket;
        } else {
          return { ...ticket, status: "Cancelado" };
        }
      }
      return ticket;
    });
    setTickets(updatedTickets);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  return (
    <table className="table-auto w-full">
      <thead>
        <tr>
          <th className="border px-4 py-2">ID Ticket</th>
          <th className="border px-4 py-2">Fecha</th>
          <th className="border px-4 py-2">Descripción</th>
          <th className="border px-4 py-2">Status</th>
          <th className="border px-4 py-2">Acciones</th>
        </tr>
      </thead>
      <tbody>
        {tickets.map((ticket) => (
          <tr key={ticket.id}>
            <td className="border px-4 py-2">{ticket.id}</td>
            <td className="border px-4 py-2">{ticket.fecha}</td>
            <td className="border px-4 py-2">{ticket.descripcion}</td>
            <td className="border px-4 py-2">{ticket.status}</td>
            <td className="border px-4 py-2">
              <button
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                onClick={() => handleConsultar(ticket)}
              >
                Consultar
              </button>
            </td>
          </tr>
        ))}
      </tbody>
      {isModalOpen && selectedTicket && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
          <div className="bg-white p-6 rounded-md">
            <h2 className="text-2xl font-bold mb-4">Detalles del Ticket</h2>
            <p>No. Ticket: {selectedTicket.id}</p>
            <p>Autor: Nombre del Autor</p>
            <p>Departamento: Nombre del Departamento</p>
            <p>Fecha: {selectedTicket.fecha}</p>
            <p>Clasificación: {selectedTicket.clasificacion}</p>
            <p>Detalles:</p>
            <textarea
              className="w-full h-24 border rounded-md p-2 mb-4"
              readOnly
              value={selectedTicket.detalles}
            />
            <p>Estatus: {selectedTicket.status}</p>
            <button
              onClick={() => confirmarCancelar(selectedTicket.id)}
              className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline mt-4 mr-2"
            >
              Cancelar
            </button>
            <button
              onClick={handleCloseModal}
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline mt-4"
            >
              Cerrar
            </button>
          </div>
        </div>
      )}
    </table>
  );
};

const App = () => {
  return (
    <div>
      <Navbar />
      <div className="container mx-auto">
        <div className="flex flex-col items-center justify-center h-screen">
          <div className="w-full">
            <div className="bg-white shadow-md rounded-md p-6">
              <h1 className="text-center text-2xl font-bold mb-4">
                Consulta de Tickets
              </h1>
              <TicketTable />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
