import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faBars,
  faHome,
  faInfo,
  faBlog,
  faEnvelope,
} from "@fortawesome/free-solid-svg-icons";

function App() {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div className="App-header">
      <header className="bg-white shadow px-6">
        <div className="flex justify-between items-center h-16 border-b relative">
          <button
            className="text-gray-400 hover:text-blue-600 hover:bg-gray-100 transition-colors rounded p-1 m-1 focus:right-2 focus:ring-slate-100"
            onClick={toggleMenu}
          >
            <FontAwesomeIcon icon={faBars} />
          </button>
          {menuOpen && (
            <div className="fixed inset-0 bg-white bg-opacity-75 z-50">
              <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-white border border-gray-200 p-4 rounded shadow">
                <DropdownItem icon={faHome} href="/">
                  Home
                </DropdownItem>
                <DropdownItem icon={faInfo} href="/about">
                  About
                </DropdownItem>
                <DropdownItem icon={faBlog} href="/blog">
                  Blog
                </DropdownItem>
                <DropdownItem icon={faEnvelope} href="/contact">
                  Contact
                </DropdownItem>
              </div>
            </div>
          )}
        </div>
      </header>
    </div>
  );
}

// Componente para enlaces de navegación desplegables
const DropdownItem = ({ icon, href, children }) => (
  <a
    href={href}
    className="flex items-center text-gray-700 hover:text-blue-600 transition-colors py-2 px-4"
  >
    <FontAwesomeIcon icon={icon} className="mr-2" />
    {children}
  </a>
);

export default App;
